CustomField.formula:
IF(
AND(
AND(CEILING(MONTH(Launch_Date__c) / 3) = CEILING(MONTH(TODAY()) / 3), YEAR(Launch_Date__c) = YEAR(TODAY())),
AND(CEILING(MONTH(End_Date__c) / 3) = CEILING(MONTH(TODAY()) / 3), YEAR(End_Date__c) = YEAR(TODAY()))
),
(End_Date__c - Launch_Date__c) + 1,
(
(
IF(
AND(CEILING(MONTH(Launch_Date__c) / 3) = CEILING(MONTH(TODAY()) / 3), YEAR(Launch_Date__c) = YEAR(TODAY())),
($Setup.Financial_Quarter__c.FQ_End_Date__c - Launch_Date__c) + 1,
IF(
AND(CEILING(MONTH(End_Date__c) / 3) = CEILING(MONTH(TODAY()) / 3), YEAR(End_Date__c) = YEAR(TODAY())),
(End_Date__c - $Setup.Financial_Quarter__c.FQ_Start_Date__c ) + 1,
IF(	AND(Launch_Date__c < $Setup.Financial_Quarter__c.FQ_Start_Date__c, End_Date__c > $Setup.Financial_Quarter__c.FQ_End_Date__c),
($Setup.Financial_Quarter__c.FQ_End_Date__c - $Setup.Financial_Quarter__c.FQ_Start_Date__c )+1,
0
)
)

)
)
)
)
